mapper.select:
SELECT a.id,a.total,a.use_money as useMoney,a.no_use_money as noUseMoney,a.collection,a.FIRST_BORROW_USE_MONEY as firstBorrowUseMoney,a.netvalue,a.DRAW_MONEY as drawMoney,a.NO_DRAW_MONEY as noDrawMoney, m.USERNAME, a.e_use_money as eUseMoney,a.e_freeze_money as eFreezeMoney,a.e_collection as eCollection FROM rocky_account a INNER JOIN rocky_member m on a.USER_ID = m.id WHERE a.total!=a.use_money+a.no_use_money+a.collection+a.FIRST_BORROW_USE_MONEY+a.e_use_money+a.e_freeze_money+a.e_collection
and m.USERNAME = #{accountValidateCnd.username}
order by a.total desc

SELECT c.id,c.REPAY_ACCOUNT as repayAccount,c.CAPITAL,c.INTEREST, m.USERNAME FROM rocky_b_collectionrecord c INNER JOIN rocky_member m on c.USER_ID = m.id WHERE c.REPAY_ACCOUNT!=c.CAPITAL+c.INTEREST
and m.USERNAME = #{accountValidateCnd.username}
AND c.status IN (0,1,2,3) order by c.REPAY_ACCOUNT desc

SELECT count(1) FROM rocky_b_collectionrecord c INNER JOIN rocky_member m on c.USER_ID = m.id WHERE c.REPAY_ACCOUNT!=c.CAPITAL+c.INTEREST
and m.USERNAME = #{accountValidateCnd.username}
AND c.status IN (0,1,2,3)

SELECT r.id,r.REPAYMENT_ACCOUNT as repaymentAccount,r.CAPITAL,r.INTEREST, m.USERNAME FROM rocky_b_repaymentrecord r INNER JOIN rocky_member m on r.USER_ID = m.id WHERE r.REPAYMENT_ACCOUNT!=r.INTEREST+r.CAPITAL
and m.USERNAME = #{accountValidateCnd.username}
AND r.status IN (0,1) order by r.REPAYMENT_ACCOUNT desc

SELECT count(1) FROM rocky_b_repaymentrecord r INNER JOIN rocky_member m on r.USER_ID = m.id WHERE r.REPAYMENT_ACCOUNT!=r.INTEREST+r.CAPITAL
and m.USERNAME = #{accountValidateCnd.username}
AND r.status IN (0,1)

SELECT t.id,t.REPAYMENT_ACCOUNT as repaymentAccount,t.ACCOUNT,t.INTEREST, m.USERNAME FROM rocky_b_tenderrecord t INNER JOIN rocky_member m on t.USER_ID = m.id WHERE t.REPAYMENT_ACCOUNT!=t.ACCOUNT+t.INTEREST AND t.status IN (0,1,2)
and m.USERNAME = #{accountValidateCnd.username}
order by t.REPAYMENT_ACCOUNT desc

SELECT c.id,c.ADVANCE_YESACCOUNT as advanceYesaccount,c.CAPITAL, m.USERNAME FROM rocky_b_collectionrecord c INNER JOIN rocky_member m on c.USER_ID = m.id WHERE c.ADVANCE_YESACCOUNT < c.CAPITAL
and m.USERNAME = #{accountValidateCnd.username}
order by c.ADVANCE_YESACCOUNT desc

SELECT a.id,a.total,a.use_money as useMoney,a.no_use_money as noUseMoney,a.collection,a.FIRST_BORROW_USE_MONEY as firstBorrowUseMoney,a.DRAW_MONEY as drawMoney,a.NO_DRAW_MONEY as noDrawMoney ,m.USERNAME FROM rocky_account a,( SELECT al.* FROM rocky_accountlog al,( SELECT MAX(l.id) AS id FROM rocky_accountlog l GROUP BY l.user_id ) temp WHERE al.id=temp.id ) t, rocky_member m WHERE a.user_id=t.user_id and a.USER_ID = m.id AND ( a.total!=t.total OR a.use_money!=t.use_money OR a.no_use_money!=t.no_use_money OR a.collection!=t.collection OR a.FIRST_BORROW_USE_MONEY!=t.FIRST_BORROW_USE_MONEY OR a.DRAW_MONEY!=t.DRAW_MONEY OR a.NO_DRAW_MONEY!=t.NO_DRAW_MONEY )
and m.USERNAME = #{accountValidateCnd.username}
order by a.total desc
