mapper.select:
    select count(1) 
    from wx_bind where STATUS=1 and REMOVE_TAG=1 and U_ID in(select id from  rocky_member where IS_FINANCIAL_USER=1 and status=0)
    
			and FROM_UNIXTIME(CREATE_TIME) >= #{createTimeStart}
	
	
			and FROM_UNIXTIME(CREATE_TIME) < DATE_ADD(DATE(#{createTimeEnd,jdbcType=TIMESTAMP}),INTERVAL 1 DAY)

select m.USERNAME as vusername,m.EMAIL as vemial,rma.MOBILENUM as vmobile,rra.REALNAME as vrealname, IF(rbl.USER_ID is null, '未锁定', '已锁定') as vlockStatus, IF(rb.USER_ID is null, 0, rb.bankcount) as vbCardNum, a.total as vtotal,a.collection as vcollection,a.use_money as vuseMoney,a.no_use_money as vnoUseMoney from rocky_member m INNER JOIN rocky_account a ON (m.id = a.USER_ID
and a.total >= #{totalStart}
and a.total <= #{totalEnd}
) LEFT JOIN rocky_realname_appro rra ON (m.id = rra.USER_ID and rra.ISPASSED = 1) LEFT JOIN rocky_mobile_appro rma ON (m.id = rma.USER_ID and rma.PASSED = 1) LEFT JOIN rocky_bankinfo_log rbl ON (m.id = rbl.USER_ID and rbl.TYPE = 0 and rbl.STATUS = 0) LEFT JOIN ( select USER_ID,COUNT(ID) bankcount from rocky_bankinfo GROUP BY USER_ID ) AS rb ON (rb.USER_ID = m.id) where m.STATUS = 0 and m.IS_FINANCIAL_USER = 1
and m.USERNAME = #{username}
and rbl.USER_ID is not null
and rbl.USER_ID is null
order by a.total desc

select m.USERNAME as vusername,m.EMAIL as vemial,rma.MOBILENUM as vmobile,rra.REALNAME as vrealname, IF(rbl.USER_ID is null, '未锁定', '已锁定') as vlockStatus, IF(rb.USER_ID is null, 0, rb.bankcount) as vbCardNum, a.total as vtotal,a.collection as vcollection,a.use_money as vuseMoney,a.no_use_money as vnoUseMoney from rocky_member m INNER JOIN rocky_account a ON (m.id = a.USER_ID
and a.total >= #{bankCardLockCnd.totalStart}
and a.total <= #{bankCardLockCnd.totalEnd}
) LEFT JOIN rocky_realname_appro rra ON (m.id = rra.USER_ID and rra.ISPASSED = 1) LEFT JOIN rocky_mobile_appro rma ON (m.id = rma.USER_ID and rma.PASSED = 1) LEFT JOIN rocky_bankinfo_log rbl ON (m.id = rbl.USER_ID and rbl.TYPE = 0 and rbl.STATUS = 0) LEFT JOIN ( select USER_ID,COUNT(ID) bankcount from rocky_bankinfo GROUP BY USER_ID ) AS rb ON (rb.USER_ID = m.id) where m.STATUS = 0 and m.IS_FINANCIAL_USER = 1
and m.USERNAME = #{bankCardLockCnd.username}
and rbl.USER_ID is not null
and rbl.USER_ID is null
order by a.total desc

 
    select count(1)  
	from rocky_member m
	INNER JOIN rocky_account a ON (m.id = a.USER_ID
		
			and a.total >= #{bankCardLockCnd.totalStart} 
		 	
		
			and a.total <= #{bankCardLockCnd.totalEnd}
		
		)
	LEFT JOIN rocky_realname_appro rra ON (m.id = rra.USER_ID and rra.ISPASSED = 1)
	LEFT JOIN rocky_mobile_appro rma ON (m.id = rma.USER_ID and rma.PASSED = 1)
	LEFT JOIN rocky_bankinfo_log rbl ON (m.id = rbl.USER_ID and rbl.TYPE = 0 and rbl.STATUS = 0)
	LEFT JOIN (
	select USER_ID,COUNT(ID) bankcount from rocky_bankinfo GROUP BY USER_ID
	) AS rb ON (rb.USER_ID = m.id)
	where m.STATUS = 0 and m.IS_FINANCIAL_USER = 1 
	
			and m.USERNAME = #{bankCardLockCnd.username}
	
	
			and rbl.USER_ID is not null
	
	
			and rbl.USER_ID is null

select af.vusername,af.vemial,af.vmobile,af.vrealname,af.vlockStatus,af.createTime,af.vtotal,af.vcollection, af.vuseMoney,af.vnoUseMoney,af.fix_total from ( select m.USERNAME as vusername,m.EMAIL as vemial,rma.MOBILENUM as vmobile,rra.REALNAME as vrealname, IF(b.W_ID is null, '未绑定', '已绑定') as vlockStatus,FROM_UNIXTIME(b.CREATE_TIME) as createTime, (a.total+IFNULL(cur.TOTAL,0)+IFNULL(fix.fixCapitalTotal,0)+IFNULL(coll.fixInvestNoTotal,0)) as vtotal, a.collection as vcollection,a.use_money as vuseMoney,a.no_use_money as vnoUseMoney, IFNULL(cur.TOTAL,0) as cur_total,(IFNULL(fix.fixCapitalTotal,0)+IFNULL(coll.fixInvestNoTotal,0)) as fix_total from rocky_member m INNER JOIN rocky_account a ON (m.id = a.USER_ID
) LEFT JOIN rocky_realname_appro rra ON (m.id = rra.USER_ID and rra.ISPASSED = 1) LEFT JOIN rocky_email_appro rea ON (m.id = rea.USER_ID and rea.CHECKED = 1) LEFT JOIN rocky_mobile_appro rma ON (m.id = rma.USER_ID and rma.PASSED = 1) LEFT JOIN wx_bind b ON (m.id = b.U_ID and b.STATUS = 1) LEFT JOIN t_cur_account cur on m.id = cur.USER_ID LEFT JOIN (SELECT USER_ID,SUM(c.ACCOUNT) as fixCapitalTotal FROM t_fix_tender_detail c where c.status in(0,1) GROUP by USER_ID) fix on m.id = fix.USER_ID LEFT JOIN (SELECT USER_ID,SUM(co.INTEREST) as fixInvestNoTotal FROM t_fix_collectionrecord co where co.status=0 GROUP by USER_ID) coll on m.id = coll.USER_ID where m.STATUS= 0 and m.IS_FINANCIAL_USER = 1
and m.USERNAME = #{bankCardLockCnd.username}
and b.W_ID is not null
and b.W_ID is null
and FROM_UNIXTIME(b.CREATE_TIME) >= #{bankCardLockCnd.createTimeStart}
and FROM_UNIXTIME(b.CREATE_TIME) < DATE_ADD(DATE(#{bankCardLockCnd.createTimeEnd,jdbcType=TIMESTAMP}),INTERVAL 1 DAY)
order by a.total desc) af where 1=1
and af.vtotal>=#{bankCardLockCnd.totalStart}
and af.vtotal <= #{bankCardLockCnd.totalEnd}

select count(1) from ( select m.USERNAME as vusername,m.EMAIL as vemial,rma.MOBILENUM as vmobile,rra.REALNAME as vrealname, IF(b.W_ID is null, '未绑定', '已绑定') as vlockStatus,FROM_UNIXTIME(b.CREATE_TIME) as createTime, (a.total+IFNULL(cur.TOTAL,0)+IFNULL(fix.fixCapitalTotal,0)+IFNULL(coll.fixInvestNoTotal,0)) as vtotal, a.collection as vcollection,a.use_money as vuseMoney,a.no_use_money as vnoUseMoney, IFNULL(cur.TOTAL,0) as cur_total,(IFNULL(fix.fixCapitalTotal,0)+IFNULL(coll.fixInvestNoTotal,0)) as fix_total from rocky_member m INNER JOIN rocky_account a ON (m.id = a.USER_ID
) LEFT JOIN rocky_realname_appro rra ON (m.id = rra.USER_ID and rra.ISPASSED = 1) LEFT JOIN rocky_email_appro rea ON (m.id = rea.USER_ID and rea.CHECKED = 1) LEFT JOIN rocky_mobile_appro rma ON (m.id = rma.USER_ID and rma.PASSED = 1) LEFT JOIN wx_bind b ON (m.id = b.U_ID and b.STATUS = 1) LEFT JOIN t_cur_account cur on m.id = cur.USER_ID LEFT JOIN (SELECT USER_ID,SUM(c.ACCOUNT) as fixCapitalTotal FROM t_fix_tender_detail c where c.status in(0,1) GROUP by USER_ID) fix on m.id = fix.USER_ID LEFT JOIN (SELECT USER_ID,SUM(co.INTEREST) as fixInvestNoTotal FROM t_fix_collectionrecord co where co.status=0 GROUP by USER_ID) coll on m.id = coll.USER_ID where m.STATUS= 0 and m.IS_FINANCIAL_USER = 1
and m.USERNAME = #{bankCardLockCnd.username}
and b.W_ID is not null
and b.W_ID is null
and FROM_UNIXTIME(b.CREATE_TIME) >= #{bankCardLockCnd.createTimeStart}
and FROM_UNIXTIME(b.CREATE_TIME) < DATE_ADD(DATE(#{bankCardLockCnd.createTimeEnd,jdbcType=TIMESTAMP}),INTERVAL 1 DAY)
order by a.total desc) af where 1=1
and af.vtotal>=#{bankCardLockCnd.totalStart}
and af.vtotal <= #{bankCardLockCnd.totalEnd}
