mapper.select:
select fixBorrow.ID, fixBorrow.CONTRACT_NO, fixBorrow.PLAN_ACCOUNT, fixBorrow.LOCK_LIMIT, repayment.INTEREST, fixBorrow.LOCK_ENDTIME, fixAccount.USE_MONEY, case when fixBorrow.LOCK_ENDTIME = CURDATE() then '0' when fixBorrow.LOCK_ENDTIME = CURDATE()+1 then '0' else '1' end as tLight from t_fix_borrow fixBorrow inner join t_fix_repaymentrecord repayment on repayment.FIX_BORROW_ID =fixBorrow.ID inner join t_fix_account fixAccount on fixAccount.FIX_BORROW_ID = fixBorrow.ID where 1=1 and fixBorrow.status = 5
and fixBorrow.LOCK_ENDTIME >= #{beginDate}
and fixBorrow.LOCK_ENDTIME <= #{endDate}
order by fixBorrow.LOCK_ENDTIME ASC,fixBorrow.CONTRACT_NO ASC

select ifnull(sum(fixBorrow.PLAN_ACCOUNT),0) capital, ifnull(sum(repayment.INTEREST),0) profit from t_fix_borrow fixBorrow inner join t_fix_repaymentrecord repayment on repayment.FIX_BORROW_ID =fixBorrow.ID inner join t_fix_account fixAccount on fixAccount.FIX_BORROW_ID = fixBorrow.ID where 1=1 and fixBorrow.status = 5
and fixBorrow.LOCK_ENDTIME >= #{beginDate}
and fixBorrow.LOCK_ENDTIME <= #{endDate}

select count(1) from t_fix_borrow fixBorrow inner join t_fix_repaymentrecord repayment on repayment.FIX_BORROW_ID = fixBorrow.ID inner join t_fix_account fixAccount on fixAccount.FIX_BORROW_ID = fixBorrow.ID where 1=1 and fixBorrow.status = 5
and fixBorrow.LOCK_ENDTIME >= #{beginDate}
and fixBorrow.LOCK_ENDTIME <= #{endDate}

SELECT fixBorrow.SUCCESS_TIME,fixBorrow.CONTRACT_NO,fixBorrow.PLAN_ACCOUNT,fixBorrow.LOCK_LIMIT,fixBorrow.ID,tender_bid_flag,tender_bid_date,fixAccount.USE_MONEY, DATEDIFF(CURDATE(),IFNULL(tender_bid_date,'2999-01-01'))as diffTenderDays from t_fix_borrow fixBorrow,t_fix_account fixAccount,(select FIX_BORROW_ID,ADDTIME from t_fix_operation_log where OPER_TYPE=3) fixOperation where fixBorrow.id = fixAccount.fix_borrow_id and fixBorrow.id = fixOperation.FIX_BORROW_ID and fixBorrow.`STATUS` = 5 and fixAccount.USE_MONEY!=0
and DATEDIFF(fixBorrow.LOCK_ENDTIME,now())>2
and fixBorrow.contract_No like concat('%',#{contractNo},'%')
and fixBorrow.PUBLISH_TIME >= #{beginDate}
and fixBorrow.PUBLISH_TIME <= #{endDate}
order by fixOperation.addtime desc

SELECT count(1) from t_fix_borrow fixBorrow,t_fix_account fixAccount where fixBorrow.id = fixAccount.fix_borrow_id and fixBorrow.`STATUS` = 5 and fixAccount.USE_MONEY!=0
and DATEDIFF(fixBorrow.LOCK_ENDTIME,now())>2
and fixBorrow.contract_No like concat('%',#{contractNo},'%')
and fixBorrow.PUBLISH_TIME >= #{beginDate}
and fixBorrow.PUBLISH_TIME <= #{endDate}

		SELECT count(1)
	 
		from rocky_b_collectionrecord b_colltion , 
		t_fix_borrow fixborrow ,
		rocky_borrow rockyborrow where  b_colltion.BORROW_ID  = rockyborrow.id 
		and b_colltion.USER_ID = fixborrow.id and b_colltion.status in(1,2)  and   fixBorrow.status in (5,7)
		
			and fixBorrow.contract_No like concat('%',#{contractNo},'%')
		
		
			and fixBorrow.status =#{status}
		
		
			and FROM_UNIXTIME(b_colltion.REPAY_YESTIME) >= #{beginDate}
		
		
			and FROM_UNIXTIME(b_colltion.REPAY_YESTIME) <= #{endDate}

SELECT fixborrow.contract_No, fixborrow.`STATUS`, fixborrow.PLAN_ACCOUNT, fixborrow.LOCK_LIMIT, fixborrow.LOCK_ENDTIME, b_colltion.REPAY_YESACCOUNT borrowCapital ,
(b_colltion.REPAY_YESACCOUNT-b_colltion.CAPITAL ) borrowInterest,
 rockyborrow.CONTRACT_NO borrowContractNo, FROM_UNIXTIME(b_colltion.REPAY_YESTIME) borrowRepayTime 
from rocky_b_collectionrecord b_colltion , 
t_fix_borrow fixborrow ,
rocky_borrow rockyborrow where  b_colltion.BORROW_ID  = rockyborrow.id 
and b_colltion.USER_ID = fixborrow.id and b_colltion.status in(1,2)   and   fixBorrow.status in (5,7)

	and fixborrow.contract_No like concat('%',#{contractNo},'%')


	and fixborrow.status =#{status}


	and FROM_UNIXTIME(b_colltion.REPAY_YESTIME) >= #{beginDate}


	and FROM_UNIXTIME(b_colltion.REPAY_YESTIME) <= #{endDate}

order by fixborrow.PUBLISH_TIME desc

SELECT count(1) from t_fix_borrow fixBorrow , rocky_member member, t_fix_collectionrecord collection where fixBorrow.id = collection.fix_borrow_id and collection.user_id = member.id and fixBorrow.status ='7'
and fixBorrow.contract_No like concat('%',#{contractNo},'%')
and member.userName like concat('%',#{userName},'%')
and collection.REPAY_YESTIME >= #{beginTime}
and collection.REPAY_YESTIME <= #{endTime}

SELECT fixBorrow.CONTRACT_NO,member.username ,collection.CAPITAL account, collection.REPAY_YESACCOUNT repaymentMoney,REPAY_YESTIME borrowRepayTime from t_fix_borrow fixBorrow , rocky_member member, t_fix_collectionrecord collection where fixBorrow.id = collection.fix_borrow_id and collection.user_id = member.id and fixBorrow.status ='7'
and fixBorrow.contract_No like concat('%',#{contractNo},'%')
and member.userName like concat('%',#{userName},'%')
and collection.REPAY_YESTIME >= #{beginTime}
and collection.REPAY_YESTIME <= #{endTime}
order by fixBorrow.PUBLISH_TIME desc,fixBorrow.CONTRACT_NO desc

		SELECT
		fixBorrow.CONTRACT_NO,fixBorrow.PLAN_ACCOUNT,ROUND(SUM(collection.REPAY_YESACCOUNT-collection.CAPITAL),2)
		totalInterest from
		t_fix_collectionrecord collection ,
		t_fix_borrow fixBorrow
		where
		collection.FIX_BORROW_ID = fixBorrow.ID and fixBorrow.CONTRACT_NO =#{contractNo}
        
			and collection.REPAY_YESTIME >= #{beginTime}
		
		
			and collection.REPAY_YESTIME <= #{endTime}

SELECT 
(select IFNULL(SUM(PLAN_ACCOUNT),0) from t_fix_borrow
where `STATUS` in(3,4,5,7,8,9,10,-1)		 

	and LOCK_LIMIT = #{lockLimit}


	and PUBLISH_TIME>=#{beginDate}


	and PUBLISH_TIME<=#{endDate}

 ) as pubMoney,
(select COUNT(*) from t_fix_borrow
where `STATUS` in(3,4,5,7,8,9,10,-1)

	and LOCK_LIMIT = #{lockLimit}


	and PUBLISH_TIME>=#{beginDate}


	and PUBLISH_TIME<=#{endDate}

 ) as pubNum,
(select IFNULL(SUM(ACCOUNT_YES),0) from t_fix_borrow
where `STATUS` in(4,5,7,8,9,10)

	and LOCK_LIMIT = #{lockLimit}


	and SUCCESS_TIME>=#{beginDate}


	and SUCCESS_TIME<=#{endDate}

 ) as sucMoney,
(select COUNT(*) from t_fix_tender_detail tftd
INNER JOIN t_fix_borrow tfb on tftd.fix_borrow_id=tfb.id
where tftd.`STATUS` in(1,3) 

	and tfb.LOCK_LIMIT = #{lockLimit}


	and tfb.SUCCESS_TIME>=#{beginDate}


	and tfb.SUCCESS_TIME<=#{endDate}

 ) as joinNum,
(select COUNT(*) FROM (
select tftd.user_id from t_fix_tender_detail tftd
INNER JOIN t_fix_borrow tfb on tftd.fix_borrow_id=tfb.id
where tftd.`STATUS` in(1,3) 

	and tfb.LOCK_LIMIT = #{lockLimit}


	and tfb.SUCCESS_TIME>=#{beginDate}


	and tfb.SUCCESS_TIME<=#{endDate}
		  
GROUP BY tftd.user_id
) t) as joinUser,
(select IFNULL(SUM(ACCOUNT_YES),0) from t_fix_borrow
where `STATUS` in(4,5,7,8,9,10) and AREA_TYPE = 0		
	
		and LOCK_LIMIT = #{lockLimit}
	
	
		and SUCCESS_TIME>=#{beginDate}
	
	
		and SUCCESS_TIME<=#{endDate}
	
 ) as sucComMoney,
(select IFNULL(SUM(ACCOUNT_YES),0) from t_fix_borrow
where `STATUS` in(4,5,7,8,9,10) and AREA_TYPE = 1
	
		and LOCK_LIMIT = #{lockLimit}
	
	
		and SUCCESS_TIME>=#{beginDate}
	
	
		and SUCCESS_TIME<=#{endDate}
			
 ) as sucNewMoney,
(select IFNULL(SUM(ACCOUNT_YES),0) from t_fix_borrow
where `STATUS` in(4,5,7,8,9,10) and AREA_TYPE = 0 and AREA_CHANGE_TIME is not NULL
	
		and LOCK_LIMIT = #{lockLimit}
	
	
		and SUCCESS_TIME>=#{beginDate}
	
	
		and SUCCESS_TIME<=#{endDate}
	
 ) as sucNewToComMoney
FROM dual

SELECT 
(select IFNULL(SUM(PLAN_ACCOUNT),0) from t_fix_borrow
where `STATUS` in(3,4,5,7,8,9,10,-1) and AREA_TYPE = 1

	and PUBLISH_TIME>=#{beginDate}


	and PUBLISH_TIME<=#{endDate}

 ) as pubMoney,
(select COUNT(*) from t_fix_borrow 
where `STATUS` in(3,4,5,7,8,9,10,-1)  and AREA_TYPE = 1

	and PUBLISH_TIME>=#{beginDate}


	and PUBLISH_TIME<=#{endDate}

 ) as pubNum,
(select IFNULL(SUM(ACCOUNT_YES),0) from t_fix_borrow
where `STATUS` in(4,5,7,8,9,10)  and AREA_TYPE = 1

	and SUCCESS_TIME>=#{beginDate}


	and SUCCESS_TIME<=#{endDate}

 ) as sucMoney,
(select COUNT(*) from t_fix_tender_detail tftd
INNER JOIN t_fix_borrow tfb on tftd.fix_borrow_id=tfb.id
where tftd.`STATUS` in(1,3)  and tfb.AREA_TYPE = 1

	and tfb.SUCCESS_TIME>=#{beginDate}


	and tfb.SUCCESS_TIME<=#{endDate}

 ) as joinNum,
(select COUNT(*) FROM (
select tftd.user_id from t_fix_tender_detail tftd
INNER JOIN t_fix_borrow tfb on tftd.fix_borrow_id=tfb.id
where tftd.`STATUS` in(1,3)  and tfb.AREA_TYPE = 1

	and tfb.SUCCESS_TIME>=#{beginDate}


	and tfb.SUCCESS_TIME<=#{endDate}
		  
GROUP BY tftd.user_id
) t) as joinUser,
0 as sucComMoney,
(select IFNULL(SUM(ACCOUNT_YES),0) from t_fix_borrow
where `STATUS` in(4,5,7,8,9,10) and AREA_TYPE = 1
	
		and SUCCESS_TIME>=#{beginDate}
	
	
		and SUCCESS_TIME<=#{endDate}
			
 ) as sucNewMoney,
 0 as sucNewToComMoney
FROM dual
