mapper.select:
select b.CONTRACT_NO, b.PLAN_ACCOUNT, c.USERNAME, a.ACCOUNT, a.ADDTIME, a.`STATUS`, a.TENDER_TYPE, a.AUTO_TENDER_TYPE, a.ROWNUM, a.AUTO_INVEST_RECORD_ID from t_fix_tender_detail a inner join t_fix_borrow b on a.FIX_BORROW_ID=b.ID inner join rocky_member c on a.USER_ID=c.ID where 1=1
and b.CONTRACT_NO = #{contractNo}
and c.USERNAME like concat('%',#{userName},'%')
and a.ADDTIME >= #{beginTime}
and a.ADDTIME <= #{endTime}
and a.`STATUS` in (0,1,2,3)
and a.`STATUS` = #{status}
order by a.ID desc

 		select count(1) 
 		from t_fix_tender_detail a
		inner join t_fix_borrow b on a.FIX_BORROW_ID=b.ID
		inner join rocky_member c on a.USER_ID=c.ID
 		where 1=1
 		
			and b.CONTRACT_NO =  #{contractNo}
		
		
			and c.USERNAME like concat('%',#{userName},'%')
		
		
			and a.ADDTIME >= #{beginTime}
		
		
			and a.ADDTIME <= #{endTime}
		
		
			and	a.`STATUS` in (0,1,2,3)
		
		
			and	a.`STATUS` = #{status}

mapper.update:
UPDATE  T_FIX_TENDER_DETAIL 
SET STATUS = ${status} 
WHERE FIX_BORROW_ID=${fixBorrowId}
