mapper.select:
select
,
, (SELECT GROUP_CONCAT(tct.NAME) FROM t_cms_article_tag tcat LEFT JOIN t_cms_tag tct ON tcat.TAG_ID =tct .ID WHERE tcat.ARTICLE_ID =#{id,jdbcType=INTEGER}) tags from t_cms_article where ID = #{id,jdbcType=INTEGER}

mapper.delete:
    update   t_cms_article  set status=1 
	    where id in 
	       
		        #{id}

mapper.sql:
where  1=1   and   tca.STATUS =0
 
  and   CHANNEL_ID =#{channelId}
 
 
  and   TITLE like concat('%',#{title},'%')  
 
  
  and   AUTHOR =#{author}
