select b.`NAME` borrow_name, b.`STATUS` borrow_status, b.style style, b.TIME_LIMIT timeLimit,b.BORROWTYPE borrowType, m.username, , (SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c WHERE c.IS_FIRST_BORROW = 1 AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`) as first_account, (SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c, t_first_tender_real tr WHERE c.FIRST_TENDER_REAL_ID = tr.ID AND c.IS_FIRST_BORROW = 1 AND FROM_UNIXTIME(c.REPAY_TIME) >= DATE_ADD(tr.ADDTIME,INTERVAL 6 MONTH) AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`) as unlockFirstAccount from rocky_b_repaymentrecord r,rocky_borrow b ,rocky_member m where r.BORROW_ID = b.id and b.BORROWTYPE in(1,2,5) and b.`STATUS` in(4,42,5) and b.USER_ID = m.id
and r.REPAYMENT_TIME >= #{beignTime}
and r.REPAYMENT_TIME <= #{endTime}
ORDER BY r.status asc, r.REPAYMENT_TIME asc

  		SELECT count(r.id)
		FROM rocky_b_repaymentrecord r,rocky_borrow b
		WHERE r.BORROW_ID = b.id and b.BORROWTYPE in(1,2,5) and b.`STATUS` in(4,42,5) 
  		
			and r.REPAYMENT_TIME >= #{beignTime}
		
		
			and r.REPAYMENT_TIME <= #{endTime}

SELECT SUM(CASE WHEN `STATUS` = 0 THEN REPAYMENT_ACCOUNT END) AS noRepaymentAccount, SUM(CASE WHEN `STATUS` = 1 THEN REPAYMENT_ACCOUNT END) AS yesRepaymentAccount, SUM(REPAYMENT_ACCOUNT) AS repaymentAccount FROM ( SELECT br.`STATUS`, br.REPAYMENT_ACCOUNT FROM rocky_b_repaymentrecord br, rocky_borrow b WHERE br.BORROW_ID = b.id AND b.BORROWTYPE IN (1, 2, 5)
AND br.REPAYMENT_TIME >= #{beignTime}
AND br.REPAYMENT_TIME <= #{endTime}
) t

SELECT ifnull(SUM(CASE WHEN c.`STATUS` IN(0,2) THEN c.CAPITAL END),0) AS noFirstAccount, ifnull(SUM(CASE WHEN c.`STATUS` IN(1,3) THEN c.CAPITAL END),0) AS yesFirstAccount, ifnull(SUM(c.CAPITAL),0) AS firstAccount FROM rocky_b_collectionrecord c,rocky_borrow b WHERE c.BORROW_ID = b.id AND b.BORROWTYPE IN (1, 2, 5)
AND c.REPAY_TIME >= #{beignTime}
AND c.REPAY_TIME <= #{endTime}
AND c.IS_FIRST_BORROW = 1 AND c.STATUS IN(0,1,2,3)

select b.`NAME` borrow_name, b.`STATUS` borrow_status, b.style style, b.TIME_LIMIT timeLimit, b.BORROWTYPE borrowType, m.username, , (SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c WHERE c.IS_FIRST_BORROW = 1 AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`) first_account, (SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c, t_first_tender_real tr WHERE c.FIRST_TENDER_REAL_ID = tr.ID AND c.IS_FIRST_BORROW = 1 AND FROM_UNIXTIME(c.REPAY_TIME) >= DATE_ADD(tr.ADDTIME,INTERVAL 6 MONTH) AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`) as unlockFirstAccount from rocky_b_repaymentrecord r,rocky_borrow b, rocky_member m where r.BORROW_ID = b.id and b.BORROWTYPE in(1, 2, 5) and b.USER_ID = m.id and ((UNIX_TIMESTAMP(CONCAT(SUBSTR(NOW() FROM 1 FOR 10),' 00:00:00')) > UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_time) FROM 1 FOR 10),' 00:00:00')) and r.`STATUS` = 0) or (r.`STATUS` = 1 AND (UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_yestime) FROM 1 FOR 10),' 00:00:00')) > UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_time) FROM 1 FOR 10),' 00:00:00')))))
and r.REPAYMENT_TIME >= #{beignTime}
and r.REPAYMENT_TIME <= #{endTime}
and r.status = #{statusStr}
ORDER BY r.status asc, r.webstatus asc, r.REPAYMENT_TIME asc

  		select count(r.id)
		from rocky_b_repaymentrecord r,rocky_borrow b
		where r.BORROW_ID = b.id and b.BORROWTYPE in(1, 2, 5)
		and ((UNIX_TIMESTAMP(CONCAT(SUBSTR(NOW() FROM 1 FOR 10),' 00:00:00')) > UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_time) FROM 1 FOR 10),' 00:00:00')) and r.`STATUS` = 0) 
		or (r.`STATUS` = 1 AND (UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_yestime) FROM 1 FOR 10),' 00:00:00')) > UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_time) FROM 1 FOR 10),' 00:00:00'))))) 
  		
			and r.REPAYMENT_TIME >= #{beignTime}
		
		
			and r.REPAYMENT_TIME <= #{endTime}
		
		
			and r.status = #{statusStr}

select count(r.id) totalCount, SUM(r.CAPITAL) totalCapital, SUM((SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c WHERE c.IS_FIRST_BORROW = 1 AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`)) totalFirstAcount, SUM(r.INTEREST) totalInterest, SUM(CASE WHEN r.status = 0 AND TIMESTAMPDIFF(DAY,DATE_FORMAT(FROM_UNIXTIME(r.REPAYMENT_TIME),'%Y-%m-%d'),CURDATE()) > 0 THEN round(r.REPAYMENT_ACCOUNT*0.002*TIMESTAMPDIFF(DAY,DATE_FORMAT(FROM_UNIXTIME(r.REPAYMENT_TIME),'%Y-%m-%d'),CURDATE()),2) ELSE r.LATE_INTEREST END) totalLateInterest from rocky_b_repaymentrecord r,rocky_borrow b, rocky_member m where r.BORROW_ID = b.id and b.BORROWTYPE in(1, 2, 5) and b.USER_ID = m.id and ((UNIX_TIMESTAMP(CONCAT(SUBSTR(NOW() FROM 1 FOR 10),' 00:00:00')) > UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_time) FROM 1 FOR 10),' 00:00:00')) and r.`STATUS` = 0) or (r.`STATUS` = 1 AND (UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_yestime) FROM 1 FOR 10),' 00:00:00')) > UNIX_TIMESTAMP(CONCAT(SUBSTR(FROM_UNIXTIME(r.repayment_time) FROM 1 FOR 10),' 00:00:00')))))
and r.REPAYMENT_TIME >= #{beignTime}
and r.REPAYMENT_TIME <= #{endTime}
and r.status = #{statusStr}
ORDER BY r.status asc, r.webstatus asc, r.REPAYMENT_TIME asc

SELECT m.USERNAME,b.`NAME` borrow_name, b.`STATUS` borrow_status, b.STYLE style, b.TIME_LIMIT timeLimit, b.BORROWTYPE borrowType,,
	(SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c WHERE c.IS_FIRST_BORROW = 1 AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`) first_account,
	(SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c, t_first_tender_real tr WHERE c.FIRST_TENDER_REAL_ID = tr.ID AND c.IS_FIRST_BORROW = 1 AND FROM_UNIXTIME(c.REPAY_TIME) >= DATE_ADD(tr.ADDTIME,INTERVAL 6 MONTH) AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`) as unlockFirstAccount
	FROM (
		SELECT t.max_id,t.USER_ID FROM ( 
			SELECT r.id max_id,t.USER_ID,t.STATUS1,t.STATUS0,t.REPAYMENT_YESTIME  FROM (
				SELECT MAX(r.`STATUS`) AS STATUS1, MIN(r.`STATUS`) AS STATUS0,MAX(r.REPAYMENT_YESTIME) AS REPAYMENT_YESTIME,r.USER_ID FROM rocky_borrow b, rocky_b_repaymentrecord r WHERE r.`STATUS` IN (0, 1) AND r.BORROW_ID = b.ID AND b.BORROWTYPE IN(1,2,5) GROUP BY b.USER_ID
			) t LEFT JOIN rocky_b_repaymentrecord r on(t.USER_ID = r.USER_ID and t.REPAYMENT_YESTIME = r.REPAYMENT_YESTIME)
		) t WHERE t.STATUS1 = 1 AND STATUS0 = 1 AND FROM_UNIXTIME(t.REPAYMENT_YESTIME,'%Y-%m-%d')   <  DATE_SUB(CURDATE(),INTERVAL 3 DAY)
	) tt,rocky_member m,rocky_borrow b,rocky_b_repaymentrecord r
	WHERE tt.USER_ID = m.ID AND tt.max_id = r.id AND r.BORROW_ID = b.ID
	
		and r.repayment_yestime >= #{beignTime}
	
	
		and r.repayment_yestime <= #{endTime}
	
	ORDER BY r.repayment_yestime desc

  		SELECT count(*) FROM (
	  		SELECT t.max_id,t.USER_ID FROM ( 
				SELECT max(r.id) max_id,b.USER_ID, MAX(r.`STATUS`) AS STATUS1, MIN(r.`STATUS`) AS STATUS0, MAX(r.REPAYMENT_YESTIME) AS REPAYMENT_YESTIME 
				FROM rocky_borrow b, rocky_b_repaymentrecord r 
				WHERE r.`STATUS` IN (0, 1) AND r.BORROW_ID = b.ID AND b.BORROWTYPE IN(1,2,5) GROUP BY b.USER_ID 
			) t WHERE t.STATUS1 = 1 AND STATUS0 = 1 AND FROM_UNIXTIME(t.REPAYMENT_YESTIME,'%Y-%m-%d')  <  DATE_SUB(CURDATE(),INTERVAL 3 DAY)
		) tt,rocky_b_repaymentrecord r
		WHERE tt.max_id = r.id
		
			and r.repayment_yestime >= #{beignTime}
		
		
			and r.repayment_yestime <= #{endTime}

SELECT count(r.id) totalCount,SUM(r.CAPITAL) totalCapital, SUM((SELECT CASE WHEN SUM(c.CAPITAL) is null then 0 else SUM(c.CAPITAL) end FROM rocky_b_collectionrecord c WHERE c.IS_FIRST_BORROW = 1 AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`)) totalFirstAcount, SUM(r.INTEREST) totalInterest, SUM(CASE WHEN r.status = 0 AND TIMESTAMPDIFF(DAY,DATE_FORMAT(FROM_UNIXTIME(r.REPAYMENT_TIME),'%Y-%m-%d'),CURDATE()) > 0 THEN round(r.REPAYMENT_ACCOUNT*0.002*TIMESTAMPDIFF(DAY,DATE_FORMAT(FROM_UNIXTIME(r.REPAYMENT_TIME),'%Y-%m-%d'),CURDATE()),2) ELSE r.LATE_INTEREST END) totalLateInterest FROM ( SELECT t.max_id,t.USER_ID FROM ( SELECT r.id max_id,t.USER_ID,t.STATUS1,t.STATUS0,t.REPAYMENT_YESTIME FROM ( SELECT MAX(r.`STATUS`) AS STATUS1, MIN(r.`STATUS`) AS STATUS0,MAX(r.REPAYMENT_YESTIME) AS REPAYMENT_YESTIME,r.USER_ID FROM rocky_borrow b, rocky_b_repaymentrecord r WHERE r.`STATUS` IN (0, 1) AND r.BORROW_ID = b.ID AND b.BORROWTYPE IN(1,2,5) GROUP BY b.USER_ID ) t LEFT JOIN rocky_b_repaymentrecord r on(t.USER_ID = r.USER_ID and t.REPAYMENT_YESTIME = r.REPAYMENT_YESTIME) ) t WHERE t.STATUS1 = 1 AND STATUS0 = 1 AND FROM_UNIXTIME(t.REPAYMENT_YESTIME,'%Y-%m-%d') < DATE_SUB(CURDATE(),INTERVAL 3 DAY) ) tt,rocky_member m,rocky_borrow b,rocky_b_repaymentrecord r WHERE tt.USER_ID = m.ID AND tt.max_id = r.id AND r.BORROW_ID = b.ID
and r.repayment_yestime >= #{beignTime}
and r.repayment_yestime <= #{endTime}
ORDER BY r.repayment_yestime desc

SELECT ifnull(SUM(CASE WHEN c.`STATUS` IN(0,2) THEN c.CAPITAL END),0) AS noFixAccount, ifnull(SUM(CASE WHEN c.`STATUS` IN(1,3) THEN c.CAPITAL END),0) AS yesFixAccount, ifnull(SUM(c.CAPITAL),0) AS fixAccount FROM rocky_b_collectionrecord c INNER JOIN rocky_borrow b on c.BORROW_ID = b.id INNER JOIN t_fix_borrow fix on c.user_id = fix.id WHERE b.BORROWTYPE IN (1, 2, 5)
AND c.REPAY_TIME >= #{beignTime}
AND c.REPAY_TIME <= #{endTime}
AND c.STATUS IN(0,1,2,3)
